crosshairlength = 27
local uis=game:GetService('UserInputService') local cc = game.Workspace.CurrentCamera; local xl = Drawing.new("Line")xl.Visible=true; local yl = Drawing.new("Line")yl.Visible=true; xl.Thickness=1; yl.Thickness=1; xl.Color=Color3.fromRGB(255,0,0); yl.Color=Color3.fromRGB(255,0,0); xl.From=Vector2.new((cc.ViewportSize.X/2)+(crosshairlength/2+1),cc.ViewportSize.Y/2) xl.To=Vector2.new(cc.ViewportSize.X/2-(crosshairlength/2),cc.ViewportSize.Y/2) yl.From=Vector2.new(cc.ViewportSize.X/2,cc.ViewportSize.Y/2+(crosshairlength/2)) yl.To=Vector2.new(cc.ViewportSize.X/2,cc.ViewportSize.Y/2-(crosshairlength/2)) function rainbow(yeet) return math.acos(math.cos(yeet*math.pi))/math.pi end local count=0 local rgb=coroutine.create(function()while wait(0.1) do pcall(function() xl.Color=Color3.fromHSV(rainbow(count),1,1) yl.Color=Color3.fromHSV(rainbow(count),1,1) count=count+0.01 end)end end) coroutine.resume(rgb) uis.InputBegan:Connect(function(i) if i.KeyCode==Enum.KeyCode.Delete then xl:Remove() yl:Remove() end end)
